local CoreGui=game:GetService("StarterGui")CoreGui:SetCore("SendNotification",{Title="XDGHOB",Text="脚本启动成功",Duration=3})print("脚本启动")local vu=game:GetService("VirtualUser")game:GetService("Players").LocalPlayer.Idled:connect(function()vu:Button2Down(Vector2.new(0,0),workspace.CurrentCamera.CFrame)wait(1)vu:Button2Up(Vector2.new(0,0),workspace.CurrentCamera.CFrame)end)task.wait(1)local player=game.Players.LocalPlayer local playerGui=player:WaitForChild("PlayerGui")local sg=Instance.new("ScreenGui")sg.Name="XDGHOBScript"sg.Parent=playerGui local openBtn=Instance.new("TextButton")openBtn.Parent=sg openBtn.Size=UDim2.new(0,100,0,40)openBtn.Position=UDim2.new(0,50,0,50)openBtn.BackgroundColor3=Color3.fromRGB(30,136,229)openBtn.Text="菜单"openBtn.TextColor3=Color3.new(1,1,1)openBtn.TextSize=14 local menu=Instance.new("Frame")menu.Parent=sg menu.Size=UDim2.new(0,250,0,400)menu.Position=UDim2.new(0,160,0,50)menu.BackgroundColor3=Color3.fromRGB(40,40,40)menu.Visible=false local scroll=Instance.new("ScrollingFrame")scroll.Parent=menu scroll.Size=UDim2.new(1,0,1,0)scroll.BackgroundTransparency=1 scroll.ScrollBarThickness=6 scroll.CanvasSize=UDim2.new(0,0,0,1000)local list=Instance.new("UIListLayout")list.Parent=scroll list.Padding=UDim.new(0,5)openBtn.MouseButton1Click:Connect(function()menu.Visible=not menu.Visible end)local UIS=game:GetService("UserInputService")local dragging=false local dragStart=Vector2.new(0,0)local btnStart=Vector2.new(0,0)openBtn.InputBegan:Connect(function(input)if input.UserInputType==Enum.UserInputType.MouseButton1 then dragging=true dragStart=Vector2.new(input.Position.X,input.Position.Y)btnStart=Vector2.new(openBtn.Position.X.Offset,openBtn.Position.Y.Offset)end end)UIS.InputChanged:Connect(function(input)if dragging and input.UserInputType==Enum.UserInputType.MouseMovement then local delta=Vector2.new(input.Position.X-dragStart.X,input.Position.Y-dragStart.Y)local newX=math.clamp(btnStart.X+delta.X,0,UIS.ViewSizeX-openBtn.Size.X.Offset)local newY=math.clamp(btnStart.Y+delta.Y,0,UIS.ViewSizeY-openBtn.Size.Y.Offset)openBtn.Position=UDim2.new(0,newX,0,newY)menu.Position=UDim2.new(0,newX+openBtn.Size.X.Offset+10,0,newY)end end)UIS.InputEnded:Connect(function(input)if input.UserInputType==Enum.UserInputType.MouseButton1 then dragging=false end end)local function addBtn(name,func)local btn=Instance.new("TextButton")btn.Parent=scroll btn.Size=UDim2.new(0,230,0,30)btn.Text=name btn.BackgroundColor3=Color3.fromRGB(60,60,60)btn.TextColor3=Color3.new(1,1,1)btn.TextSize=12 btn.MouseButton1Click:Connect(func)end addBtn("光影",function()loadstring(game:HttpGet("https://pastebin.com/raw/arzRCgwS"))()end)addBtn("建筑工具",function()local hammer=Instance.new("HopperBin")hammer.Name="锤子"hammer.BinType=4 hammer.Parent=player.Backpack local clone=Instance.new("HopperBin")clone.Name="克隆"clone.BinType=3 clone.Parent=player.Backpack local grab=Instance.new("HopperBin")grab.Name="抓取"grab.BinType=2 grab.Parent=player.Backpack end)addBtn("夜视",function()if game.Lighting.Ambient==Color3.new(1,1,1)then game.Lighting.Ambient=Color3.new(0,0,0)else game.Lighting.Ambient=Color3.new(1,1,1)end end)addBtn("飞行",function()loadstring(game:HttpGet("https://pastebin.com/raw/U27yQRxS"))()end)addBtn("无敌",function()loadstring(game:HttpGet("https://pastebin.com/raw/H3RLCWWZ"))()end)addBtn("复制群号",function()setclipboard("361097232")CoreGui:SetCore("SendNotification",{Title="成功",Text="已复制群号361097232",Duration=3})end)print("菜单创建完成")